import { useState } from 'react'
import { ColumnDef } from "@tanstack/react-table"
import { Button } from "@/registry/new-york/ui/button"
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/registry/new-york/ui/dropdown-menu"
import { Alert, AlertDescription, AlertTitle } from "@/registry/new-york/ui/alert"
import { RefreshCw, ChevronDown, Plus } from "lucide-react"
import { ExclamationTriangleIcon } from "@radix-ui/react-icons"
import { DataTable } from "@/registry/new-york/blocks/resource-view/data-table"
import { KeyedMutator } from 'swr'
import { useTranslations } from 'next-intl'

interface Action<TData> {
  label: string
  handler: (selectedRows: TData[]) => void
  isDisabled?: (selectedRows: TData[]) => boolean
}

interface ResourceViewProps<TData extends object> {
  title: string
  columns: ColumnDef<TData>[]
  data: TData[] | undefined
  mutate: KeyedMutator<TData[]>
  onCreate?: () => void
  actions: Action<TData>[]
  isLoading: boolean
  error: Error | undefined
}

export function ResourceView<TData extends object>({
  title,
  columns,
  data,
  mutate,
  onCreate,
  actions,
  isLoading,
  error,
}: ResourceViewProps<TData>) {
  const [selectedRows, setSelectedRows] = useState<TData[]>([])
  const [isRefreshing, setIsRefreshing] = useState(false)
  const t = useTranslations('component.resource-view')

  const handleRefresh = async () => {
    setIsRefreshing(true)
    try {
      await mutate()
    } catch (error) {
      console.error('Refresh failed:', error)
    } finally {
      setIsRefreshing(false)
    }
  }

  if (error) {
    return (
      <Alert variant="destructive">
        <ExclamationTriangleIcon className="h-4 w-4" />
        <AlertTitle>{t('error')}</AlertTitle>
        <AlertDescription>{t('fetcherror')}</AlertDescription>
      </Alert>
    )
  }

  return (
    <div className="pt-4">
      <div className="flex flex-col sm:flex-row sm:justify-between sm:items-center mb-4 space-y-2 sm:space-y-0">
        <h1 className="text-2xl font-bold">{title}</h1>
        <div className="flex space-x-2">
          <Button variant="outline" size="sm" className="h-9 w-9 p-0" onClick={handleRefresh} disabled={isRefreshing || isLoading}>
            <RefreshCw className={`h-4 w-4 ${isRefreshing ? 'animate-spin' : ''}`} />
          </Button>
          {actions.length > 0 && (
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button size="sm" variant="outline" disabled={isLoading || isRefreshing}>
                <ChevronDown className="mr-2 h-4 w-4" />{t('action')}
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end">
                {actions.map((action, index) => (
                  <DropdownMenuItem
                    key={index}
                    onClick={() => action.handler(selectedRows)}
                    disabled={action.isDisabled ? action.isDisabled(selectedRows) : false}
                  >
                    {action.label}
                  </DropdownMenuItem>
                ))}
              </DropdownMenuContent>
            </DropdownMenu>
          )}
          {onCreate && (
            <Button size="sm" onClick={onCreate} disabled={isLoading || isRefreshing}>
              <Plus className="mr-2 h-4 w-4" />{t('create')}
            </Button>
          )}
        </div>
      </div>

      <DataTable
        columns={columns}
        data={data || []}
        onRowSelectionChange={setSelectedRows}
        isLoading={isLoading || isRefreshing}
      />
    </div>
  )
}

import { useState, useEffect, useCallback } from 'react'
import { useTranslations } from 'next-intl'
import {
  ColumnDef,
  flexRender,
  getCoreRowModel,
  useReactTable,
  getPaginationRowModel,
  getSortedRowModel,
  getFilteredRowModel,
  SortingState,
  VisibilityState,
} from "@tanstack/react-table"
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/registry/new-york/ui/table"
import { Checkbox } from "@/registry/new-york/ui/checkbox"
import { Button } from "@/registry/new-york/ui/button"
import { Separator } from '@/registry/new-york/ui/separator'
import { Loader2, ChevronRight, ChevronLeft } from 'lucide-react'
import { TableSetting } from "@/registry/new-york/blocks/resource-view/table-setting"
import { TableSearch } from "@/registry/new-york/blocks/resource-view/table-search"

interface DataTableProps<TData, TValue> {
  columns: ColumnDef<TData, TValue>[]
  data: TData[]
  onRowSelectionChange?: (selectedRows: TData[]) => void
  isLoading?: boolean
}

export function DataTable<TData, TValue>({
  columns,
  data,
  onRowSelectionChange,
  isLoading = false,
}: DataTableProps<TData, TValue>) {
  const [sorting, setSorting] = useState<SortingState>([])
  const [rowSelection, setRowSelection] = useState({})
  const [pageSize, setPageSize] = useState(10)
  const [columnVisibility, setColumnVisibility] = useState<VisibilityState>({})
  const [globalFilter, setGlobalFilter] = useState("")
  const t = useTranslations('component.data-table')

  const table = useReactTable({
    data,
    columns,
    getCoreRowModel: getCoreRowModel(),
    getPaginationRowModel: getPaginationRowModel(),
    getSortedRowModel: getSortedRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    onSortingChange: setSorting,
    onRowSelectionChange: setRowSelection,
    onColumnVisibilityChange: setColumnVisibility,
    state: {
      sorting,
      rowSelection,
      columnVisibility,
      globalFilter,
    },
    initialState: {
      pagination: { pageSize },
    },
  })

  useEffect(() => {
    if (onRowSelectionChange) {
      const selectedRows = table.getFilteredSelectedRowModel().rows.map(row => row.original)
      onRowSelectionChange(selectedRows)
    }
  }, [rowSelection, onRowSelectionChange, table])

  const handleApplySettings = useCallback((newPageSize: number, newColumnVisibility: VisibilityState) => {
    setPageSize(newPageSize)
    table.setPageSize(newPageSize)
    setColumnVisibility(newColumnVisibility)
  }, [table])

  const renderPagination = useCallback(() => {
    const currentPage = table.getState().pagination.pageIndex + 1
    const totalPages = table.getPageCount()

    return (
      <div className="flex items-center gap-2">
        <Button
          variant="ghost"
          size="sm"
          onClick={() => table.previousPage()}
          disabled={!table.getCanPreviousPage()}
        >
          <ChevronLeft className="h-4 w-4" />
        </Button>
        <div className="flex items-center text-sm text-muted-foreground whitespace-nowrap min-w-[48px] justify-center">
          <span>{currentPage}</span>
          <span className="mx-1">/</span>
          <span>{totalPages}</span>
        </div>
        <Button
          variant="ghost"
          size="sm"
          onClick={() => table.nextPage()}
          disabled={!table.getCanNextPage()}
        >
          <ChevronRight className="h-4 w-4" />
        </Button>
      </div>
    )
  }, [table])

  const renderTableBody = useCallback(() => {
    if (isLoading) {
      return (
        <TableRow>
          <TableCell colSpan={columns.length + 1} className="h-24">
            <div className="flex justify-center items-center">
              <Loader2 className="h-6 w-6 animate-spin text-primary" />
            </div>
          </TableCell>
        </TableRow>
      )
    }

    if (table.getRowModel().rows?.length) {
      return table.getRowModel().rows.map((row) => (
        <TableRow key={row.id} data-state={row.getIsSelected() && "selected"}>
          <TableCell>
            <Checkbox
              checked={row.getIsSelected()}
              onCheckedChange={(value) => row.toggleSelected(!!value)}
              aria-label="Select row"
            />
          </TableCell>
          {row.getVisibleCells().map((cell) => (
            <TableCell key={cell.id} className="whitespace-nowrap">
              {flexRender(cell.column.columnDef.cell, cell.getContext())}
            </TableCell>
          ))}
        </TableRow>
      ))
    }

    return (
      <TableRow>
        <TableCell colSpan={columns.length + 1} className="h-24 text-center">
          {t('noresults')}
        </TableCell>
      </TableRow>
    )
  }, [isLoading, columns, table])

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between mb-2 gap-4 flex-wrap">
        <TableSearch
          value={globalFilter}
          onChange={setGlobalFilter}
        />
        <div className="flex items-center gap-2">
          {renderPagination()}
          <Separator orientation="vertical" className="h-4" />
          <TableSetting
            columns={table.getAllColumns()}
            pageSize={pageSize}
            columnVisibility={columnVisibility}
            onApplySettings={handleApplySettings}
          />
        </div>
      </div>
      <Table>
        <TableHeader>
          <TableRow>
            <TableHead>
              <Checkbox
                checked={table.getIsAllPageRowsSelected()}
                onCheckedChange={(value) => table.toggleAllPageRowsSelected(!!value)}
                aria-label="Select all"
                disabled={isLoading}
              />
            </TableHead>
            {table.getHeaderGroups().map((headerGroup) =>
              headerGroup.headers.map((header) => {
                const column = header.column;
                return column.getIsVisible() ? (
                  <TableHead key={header.id} className="whitespace-nowrap">
                    {header.isPlaceholder
                      ? null
                      : flexRender(
                        header.column.columnDef.header,
                        header.getContext()
                      )}
                  </TableHead>
                ) : null;
              })
            )}
          </TableRow>
        </TableHeader>
        <TableBody>
          {renderTableBody()}
        </TableBody>
      </Table>
      <div className="flex items-center space-x-2">
        <p className="text-sm text-muted-foreground">
          {t('rowselect', {
            selected: table.getFilteredSelectedRowModel().rows.length,
            total: table.getFilteredRowModel().rows.length
          })}
        </p>
      </div>
    </div>
  )
}

import { useState } from 'react'
import { Column } from "@tanstack/react-table"
import { Button } from "@/registry/new-york/ui/button"
import { Input } from "@/registry/new-york/ui/input"
import { RadioGroup, RadioGroupItem } from "@/registry/new-york/ui/radio-group"
import { Switch } from "@/registry/new-york/ui/switch"
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  DialogFooter,
} from "@/registry/new-york/ui/dialog"
import { Label } from "@/registry/new-york/ui/label"
import { Settings } from 'lucide-react'
import { useTranslations } from 'next-intl'

interface TableSettingProps {
  columns: Column<any, unknown>[]
  pageSize: number
  columnVisibility: Record<string, boolean>
  onApplySettings: (newPageSize: number, newColumnVisibility: Record<string, boolean>) => void
}

export function TableSetting({
  columns,
  pageSize,
  columnVisibility,
  onApplySettings
}: TableSettingProps) {
  const [isOpen, setIsOpen] = useState(false)
  const [tempPageSize, setTempPageSize] = useState<number | 'custom'>(pageSize)
  const [tempColumnVisibility, setTempColumnVisibility] = useState(columnVisibility)
  const [customPageSize, setCustomPageSize] = useState('')
  const t = useTranslations('component.table-setting')

  const handleApply = () => {
    const newPageSize = tempPageSize === 'custom' ? parseInt(customPageSize, 10) : tempPageSize
    if (!isNaN(newPageSize) && newPageSize > 0) {
      onApplySettings(newPageSize, tempColumnVisibility)
    }
    setIsOpen(false)
  }

  const handleCancel = () => {
    setTempPageSize(pageSize)
    setTempColumnVisibility(columnVisibility)
    setCustomPageSize('')
    setIsOpen(false)
  }

  const handleColumnVisibilityChange = (columnId: string, isVisible: boolean) => {
    setTempColumnVisibility(prev => ({ ...prev, [columnId]: isVisible }))
  }

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        <Button variant="ghost" size="icon">
          <Settings className="h-4 w-4" />
        </Button>
      </DialogTrigger>
      <DialogContent className="sm:max-w-[700px]">
        <DialogHeader>
          <DialogTitle>{t('title')}</DialogTitle>
        </DialogHeader>
        <div className="flex gap-4 py-4">
          <div className="w-1/2 pr-4 border-r">
            <h3 className="text-lg font-semibold mb-4">{t('pagesize')}</h3>
            <RadioGroup
              value={tempPageSize.toString()}
              onValueChange={(value) => {
                setTempPageSize(value === 'custom' ? 'custom' : parseInt(value, 10))
              }}
            >
              {[10, 20, 30].map((size) => (
                <div key={size} className="flex items-center space-x-2">
                  <RadioGroupItem value={size.toString()} id={`size-${size}`} />
                  <Label htmlFor={`size-${size}`}>{size}{t('row')}</Label>
                </div>
              ))}
              <div className="flex items-center space-x-2">
                <RadioGroupItem value="custom" id="size-custom" />
                <Label htmlFor="size-custom">{t('custom')}:</Label>
                <Input
                  type="number"
                  value={customPageSize}
                  onChange={(e) => setCustomPageSize(e.target.value)}
                  className="w-20"
                  min="1"
                />
              </div>
            </RadioGroup>
          </div>
          <div className="w-1/2 pl-4">
            <h3 className="text-lg font-semibold mb-4">{t('column')}</h3>
            {columns.map((column) => (
              <div key={column.id} className="flex items-center justify-between py-2">
                <Label htmlFor={`column-${column.id}`}>
                  {column.columnDef.header && typeof column.columnDef.header === 'string'
                    ? column.columnDef.header
                    : column.id}
                </Label>
                <Switch
                  checked={tempColumnVisibility[column.id] !== false}
                  onCheckedChange={(value) => handleColumnVisibilityChange(column.id, value)}
                  id={`column-${column.id}`}
                />
              </div>
            ))}
          </div>
        </div>
        <DialogFooter>
          <Button variant="ghost" onClick={handleCancel}>{t('cancel')}</Button>
          <Button onClick={handleApply}>{t('apply')}</Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  )
}

import { Input } from "@/registry/new-york/ui/input"
import { Search, X } from "lucide-react"
import { Button } from "@/registry/new-york/ui/button"

interface TableSearchProps {
  value: string
  onChange: (value: string) => void
  placeholder?: string
}

export function TableSearch({
  value,
  onChange,
  placeholder = 'Search'
}: TableSearchProps) {
  return (
    <div className="relative">
      <Search className="absolute left-2 top-2 h-4 w-4 text-muted-foreground" />
      <Input
        placeholder={placeholder}
        value={value}
        onChange={(e) => onChange(e.target.value)}
        className="pl-8 pr-8 h-8"
      />
      {value && (
        <Button
          variant="ghost"
          size="sm"
          className="absolute right-1 top-1.5 h-6 w-6 p-0 hover:bg-transparent"
          onClick={() => onChange("")}
        >
          <X className="h-4 w-4" />
        </Button>
      )}
    </div>
  )
}

