          <h3 className="text-lg font-semibold mb-2">{t('author')}</h3>
          <p>{t('user')} ID: {post.userId}</p>
        </div>
      </div>
    )
  }

  return (
    <ResourceDetail<Resource, EmptyRelatedType>
